mapper.select:
SELECT PET_NO , PET_TITLE, PET_CONTENT ,PET_PHOTO,PET_LINK, PET_DATE,PET_DEAD, USER_ID,PET_PERSON
FROM CHUNGWON
WHERE PET_NO = #{pet_no}

mapper.update:
UPDATE CHUNGWON 
SET PET_TITLE=#{pet_title} ,PET_CONTENT=#{pet_content},PET_PHOTO=#{pet_photo},PET_LINK=#{pet_link},PET_DEAD=#{pet_dead},PET_PERSON=#{pet_person}
WHERE PET_NO=#{pet_no}
