mapper.select:
SELECT COUNT(*)
FROM CHUNGWON
WHERE USER_ID = #{user_id}

SELECT COUNT(*)
FROM TOGETHER
WHERE USER_ID = #{user_id}

mapper.update:
UPDATE USER_TB
USER_PW = #{user_pw}, USER_NAME = #{user_name}, USER_NICK = #{user_nick}, USER_PHONE = #{user_phone}, USER_ADDR = #{user_addr}, USER_PIC = #{user_pic}, USER_STATUS = #{user_status}
WHERE USER_ID = #{user_id}
